const input = $input.first().json;

const config = {
  workflowId: input.workflowId || input.body?.workflowId || null,
  n8nApiUrl: input.n8nApiUrl || input.body?.n8nApiUrl || 'http://localhost:5678/api/v1',
  n8nApiKey: input.n8nApiKey || input.body?.n8nApiKey || '',
  dryRun: input.dryRun ?? input.body?.dryRun ?? false
};

if (!config.workflowId) {
  throw new Error('workflowId is required. Pass it in the input or request body.');
}

if (!config.n8nApiKey) {
  throw new Error('n8nApiKey is required. Pass it in the input.');
}

return { json: config };

const config = $('Parse Configuration').first().json;
const workflow = $input.first().json;

function simpleHash(str) {
  let hash = 5381;
  for (let i = 0; i < str.length; i++) {
    hash = ((hash << 5) + hash) + str.charCodeAt(i);
    hash = hash & hash;
  }
  return Math.abs(hash).toString(16);
}

function hashNode(node) {
  const nodeForHash = { ...node };
  if (nodeForHash.parameters?._versionControl) {
    const params = { ...nodeForHash.parameters };
    delete params._versionControl;
    nodeForHash.parameters = params;
  }
  delete nodeForHash.notes;
  return simpleHash(JSON.stringify(nodeForHash));
}

function incrementVersion(version, changeType) {
  const parts = (version || '1.0.0').split('.').map(Number);
  if (changeType === 'major') return `${parts[0] + 1}.0.0`;
  if (changeType === 'minor') return `${parts[0]}.${parts[1] + 1}.0`;
  return `${parts[0]}.${parts[1]}.${parts[2] + 1}`;
}

function formatDate(isoString) {
  const date = new Date(isoString);
  return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: 'numeric', hour: '2-digit', minute: '2-digit' });
}

function buildNodeNotes(vc, typeVer) {
  const lines = [`üìå Version: ${vc.version}`, `üìÖ Modified: ${formatDate(vc.lastModified)}`, `üîß Node Type: v${typeVer || 'N/A'}`];
  if (vc.created && vc.created !== vc.lastModified) lines.push(`üÜï Created: ${formatDate(vc.created)}`);
  return lines.join('\n');
}

function cleanNode(node) {
  const cleaned = { id: node.id, name: node.name, type: node.type, typeVersion: node.typeVersion, position: node.position, parameters: node.parameters };
  if (node.credentials) cleaned.credentials = node.credentials;
  if (node.disabled) cleaned.disabled = node.disabled;
  if (node.notes) cleaned.notes = node.notes;
  if (node.notesInFlow) cleaned.notesInFlow = node.notesInFlow;
  if (node.webhookId) cleaned.webhookId = node.webhookId;
  return cleaned;
}

const timestamp = new Date().toISOString();
const nodeChanges = [];
const updatedNodes = [];

for (const node of workflow.nodes) {
  const currentHash = hashNode(node);
  const vc = node.parameters?._versionControl || { version: '1.0.0', hash: null, lastModified: null, history: [] };
  const previousHash = vc.hash;
  const hasChanged = previousHash && previousHash !== currentHash;
  const isNew = !previousHash;
  let newVc = { ...vc };

  if (isNew) {
    newVc = { nodeTypeVersion: node.typeVersion, contentVersion: '1.0.0', version: '1.0.0', hash: currentHash, created: timestamp, lastModified: timestamp, history: [{ version: '1.0.0', timestamp, action: 'created', hash: currentHash }] };
    nodeChanges.push({ nodeName: node.name, nodeId: node.id, action: 'created', version: '1.0.0', changes: [] });
  } else if (hasChanged) {
    const newVersion = incrementVersion(vc.version, 'patch');
    newVc = { ...vc, nodeTypeVersion: node.typeVersion, contentVersion: newVersion, version: newVersion, hash: currentHash, lastModified: timestamp, history: [...vc.history.slice(-9), { version: newVersion, timestamp, action: 'modified', previousHash, hash: currentHash }] };
    nodeChanges.push({ nodeName: node.name, nodeId: node.id, action: 'modified', previousVersion: vc.version, version: newVersion, changes: [] });
  }

  const updatedNode = cleanNode({ ...node, notes: buildNodeNotes(newVc, node.typeVersion), parameters: { ...node.parameters, _versionControl: newVc } });
  updatedNodes.push(updatedNode);
}

// Calculate workflow version from node versions
let wfNewVersion = '1.0.0';
const allVersions = updatedNodes.map(n => n.parameters?._versionControl?.version || '1.0.0');
if (allVersions.length > 0) {
  const maxParts = allVersions.map(v => v.split('.').map(Number)).reduce((max, cur) => {
    if (cur[0] > max[0]) return cur;
    if (cur[0] === max[0] && cur[1] > max[1]) return cur;
    if (cur[0] === max[0] && cur[1] === max[1] && cur[2] > max[2]) return cur;
    return max;
  }, [1, 0, 0]);
  wfNewVersion = maxParts.join('.');
}

// Build clean settings - only allowed properties, NO custom fields
const cleanSettings = {};
if (workflow.settings?.errorWorkflow) cleanSettings.errorWorkflow = workflow.settings.errorWorkflow;
if (workflow.settings?.timezone) cleanSettings.timezone = workflow.settings.timezone;
if (workflow.settings?.saveManualExecutions !== undefined) cleanSettings.saveManualExecutions = workflow.settings.saveManualExecutions;
if (workflow.settings?.saveExecutionProgress !== undefined) cleanSettings.saveExecutionProgress = workflow.settings.saveExecutionProgress;
if (workflow.settings?.saveDataErrorExecution) cleanSettings.saveDataErrorExecution = workflow.settings.saveDataErrorExecution;
if (workflow.settings?.saveDataSuccessExecution) cleanSettings.saveDataSuccessExecution = workflow.settings.saveDataSuccessExecution;
if (workflow.settings?.executionTimeout !== undefined) cleanSettings.executionTimeout = workflow.settings.executionTimeout;

const updatedWorkflow = { name: workflow.name, nodes: updatedNodes, connections: workflow.connections, settings: cleanSettings };

return { json: { config, originalWorkflow: workflow, updatedWorkflow, nodeChanges, hasChanges: nodeChanges.length > 0, workflowVersion: { previous: '1.0.0', current: wfNewVersion }, timestamp } };
