const result = $input.first().json;

if (!result || result.hasChanges === undefined) {
  return {
    json: {
      status: "‚ÑπÔ∏è NO CHANGES",
      message: "No result from version control subworkflow",
      action: "Check subworkflow execution",
      openWorkflowUrl: null,
    }
  };
}

const changesList = result.nodeChanges?.map((c) => {
  if (c.action === 'created') {
    return `‚ûï ${c.nodeName}: v${c.version} (new)`;
  }

  if (c.action === 'modified') {
    let icon = '‚úèÔ∏è';
    let badge = '';

    if (c.changeType === 'major') {
      icon = 'üö®';
      badge = ' [MAJOR]';
    } else if (c.changeType === 'minor') {
      icon = '‚ö†Ô∏è';
      badge = ' [MINOR]';
    }

    const reasonLine = c.reason ? `\n   ‚îî‚îÄ ${c.reason}` : '';
    return `${icon} ${c.nodeName}: v${c.previousVersion} ‚Üí v${c.version}${badge}${reasonLine}`;
  }

  return `${c.nodeName}: v${c.version}`;
}).join('\n') || 'No changes detected';

const majorCount = result.nodeChanges?.filter((c) => c.changeType === 'major').length || 0;
const minorCount = result.nodeChanges?.filter((c) => c.changeType === 'minor').length || 0;
const patchCount = result.nodeChanges?.filter((c) => c.changeType === 'patch').length || 0;

const summaryParts = [];
if (majorCount > 0) summaryParts.push(`${majorCount} MAJOR`);
if (minorCount > 0) summaryParts.push(`${minorCount} MINOR`);
if (patchCount > 0) summaryParts.push(`${patchCount} PATCH`);

const changeSummary = summaryParts.length > 0
  ? ` (${summaryParts.join(', ')})`
  : '';

const openWorkflowUrl = result.openWorkflowUrl || null;
const git = result.git || {};

const commitHashShort = git.commit?.hash ? String(git.commit.hash).slice(0, 8) : null;
const gitStatus = git.enabled
  ? `Git ${git.committed ? 'committed' : 'not committed'}${git.remotePushEnabled ? (git.pushed ? ' + pushed' : ' (push not enabled/completed)') : ''}`
  : 'Git disabled';

let tip = "üí° Tip: Open the workflow URL below to load the latest version without manual refresh.";
if (majorCount > 0) {
  tip = "‚ö†Ô∏è MAJOR changes detected! Review carefully before activating.";
} else if (minorCount > 0) {
  tip = "üí° MINOR changes detected. Re-test key paths after update.";
}

return {
  json: {
    status: result.hasChanges ? "‚úÖ SUCCESS" : "‚ÑπÔ∏è NO CHANGES",
    message: result.hasChanges
      ? `Version control updated successfully!${changeSummary}`
      : "No node changes detected - all versions remain the same",
    action: openWorkflowUrl
      ? `Open updated workflow: ${openWorkflowUrl}`
      : (result.hasChanges
        ? "Update completed. Open the workflow again from the list to load latest state."
        : "No action needed"),
    openWorkflowUrl,
    details: {
      workflowId: result.workflowId || null,
      workflowName: result.workflowName || result.originalWorkflow?.name || "Unknown",
      totalNodes: result.updatedWorkflow?.nodes?.length || 0,
      changedNodes: result.nodeChanges?.length || 0,
      majorChanges: majorCount,
      minorChanges: minorCount,
      patchChanges: patchCount,
      gitStatus,
      gitRepoPath: git.repoPath || null,
      gitFilePath: git.filePath || null,
      gitBranch: git.branch || null,
      gitCommitHash: commitHashShort,
      timestamp: new Date().toLocaleString('en-US', {
        month: 'short',
        day: 'numeric',
        year: 'numeric',
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit',
      }),
    },
    changes: changesList,
    git,
    tip,
  },
};
